import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs'
import Typist from 'react-typist';
import Prismic from "prismic-javascript";

const Wrapper = styled.div`

    display: flex;
    position: relative;
    width: 100%;
    flex-direction: column;
    overflow: scroll-y;

`

const Hero = styled.div`

    height: 500px;
    background: url(https://parkletsband.com/img/parkletsillustrations-01.jpg);
    background-size: cover;
    background-position: center;

`

const AlbumReviewContainer = styled.div`

    display: flex;
    flex-direction: column;
    justify-content: center;
`

const AlbumReview = styled.div`

    display: flex;
    margin: 0 auto;
    h1, h2, h3, h4, p {
        margin: 0;
    }
    .albumInfo {
        margin: 20px 20px 20px 0;
        max-width: 700px;
    }
    .albumCover {
        height: 200px;
        width: 200px;
        background: ${props => (props.cover ? "url(" + props.cover
        + ")" : "lightgray")};
        background-size: cover;
        margin: 20px;
    }
    h3 {
        font-size: 40px;
    }

    .line1 {
        display: flex;
        align-items: baseline;
        h4 {
            margin-left: 10px;
            font-size: 20px;
        }
    }

    .line2 {
        display: flex;
        justify-content: space-between;
        text-align: right;
    }

`

const Criterion = styled.div`

    display: flex;
    justify-content: space-between;
    border-bottom: 1px solid rgba(0,0,0,.3);

    .tab {
        padding: 10px;
        padding: 10px 20px 8px;
    }

    .categories {
        display: flex;
    }

`

const AlbumContainer = styled.div`

    display: flex;
    max-width: 800px;

    .albumHitArea:hover + .albumCover{
        opacity: .5;
    }

    .albumHitArea:hover ~ .albumTitle {
        opacity: 1;
    }

`

const AlbumHitArea = styled.div`

    position: absolute;
    height: 200px;
    width: 200px;
    z-index: 100;

` 

const AlbumCover = styled.div`

    position: absolute;
    height: 200px;
    width: 200px;
    background: ${props => (props.cover ? "url(" + props.cover
        + ")" : "lightgray")};
    background-size: cover;

`

const AlbumTile = styled.div`

    height: 200px;
    width: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;

    h2 {
        font-weight: normal;
        opacity: 0;
        z-index: 20;
        position: absolute;
        margin: 0;
    }

    .albumCover{
        z-index: 1;
    }


`

class Charts extends Component {

    state = {
        articles: []
    }

    loadArticles = () => {
        const apiEndpoint = "https://sutromusic.cdn.prismic.io/api/v2";

        Prismic.api(apiEndpoint).then(api => {
            api.query(
                Prismic.Predicates.at("document.type", "article")
            ).then(response => {
                if (response) {
                    this.setState({ articles: response.results });

                }
            });
        });

    };

    componentDidUpdate() {
        console.log(this.state.articles)
    }

    componentDidMount() {
        this.loadArticles()
    }

    openSpotify = (url) => {
        window.open(url);
    }

    render() {
        return (
            <Wrapper className="wrapper">
                <Hero></Hero>
                <AlbumContainer>
                    {this.state.articles && this.state.articles.map((albumReview, i) => (
                        <AlbumTile className="albumTile"
                            onClick={() => this.openSpotify(albumReview.data.spotify[0].text)}
                        >
                            <AlbumHitArea className="albumHitArea">

                            </AlbumHitArea>
                            <AlbumCover className='albumCover' cover={albumReview.data.albumcover[0].text} ></AlbumCover>
                            <h2 className="albumTitle">{albumReview.data.songname[0].text}</h2>

                        </AlbumTile>
                    )
                    )}
                </AlbumContainer>
                <AlbumReviewContainer>
                    <Criterion>
                        <div>

                        </div>
                        <div className="categories">
                            <div className="tab">Indie</div>
                            <div className="tab">Electronic</div>
                            <div className="tab">Hip Hop</div>
                        </div>
                    </Criterion>

                    {this.state.articles && this.state.articles.map((albumReview, i) => (
                        <AlbumReview cover={albumReview.data.albumcover[0].text}>
                            <div className="albumCover" >
                            </div>
                            <div className="albumInfo">
                                {console.log(albumReview.data.albumcover[0].text)}
                                <div className="line2">
                                    <div className="line1">
                                        <h3>{albumReview.data.songname[0].text}</h3>
                                        <h4>by {albumReview.data.bandname[0].text}</h4>
                                    </div>
                                    <div>
                                        <p>{albumReview.data.date[0].text}</p>
                                        <p>{albumReview.data.rating[0].text}</p>
                                    </div>
                                </div>
                                <p>{albumReview.data.review[0].text}</p>
                            </div>

                        </AlbumReview>
                    ))}
                </AlbumReviewContainer>
            </Wrapper>
        );
    }

}

export default Charts;
