import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs'
import Typist from 'react-typist';
import Prismic from "prismic-javascript";

const Wrapper = styled.div`

    display: flex;
    position: relative;
    width: 100%;
    flex-direction: column;
    overflow: scroll-y;

`

const Hero = styled.div`

    height: 500px;
    background: url(https://parkletsband.com/img/parkletsillustrations-01.jpg);
    background-size: cover;
    background-position: center;

`

const AlbumReviewContainer = styled.div`

    display: flex;
    flex-direction: column;
    justify-content: center;
`

const AlbumReview = styled.div`

    display: flex;
    margin: 0 auto;
    h1, h2, h3, h4, p {
        margin: 0;
    }
    .albumInfo {
        margin: 20px 20px 20px 0;
        max-width: 700px;
    }
    .albumCover {
        height: 200px;
        width: 200px;
        background: ${props => (props.cover ? "url(" + props.cover
        + ")" : "lightgray")};
        background-size: cover;
        margin: 20px;
    }
    h3 {
        font-size: 40px;
    }

    p {
        font-size: 15px;
        line-height: 20px;
    }

    .line1 {
        display: flex;
        align-items: baseline;
        h4 {
            margin-left: 10px;
            font-size: 20px;
        }
    }

    .line2 {
        display: flex;
        justify-content: space-between;
        text-align: right;
    }

`

const Criterion = styled.div`

    display: flex;
    justify-content: space-between;
    border-bottom: 1px solid rgba(0,0,0,.3);

    .tab {
        padding: 10px;
        padding: 10px 20px 8px;
    }

    .categories {
        display: flex;
    }

    .tab:hover {
        background-color: yellow;
        cursor: pointer;
    }

`

const AlbumContainer = styled.div`

    display: flex;
    justify-content: center;

    .albumHitArea:hover + .albumCover{
        opacity: .5;
    }

    .albumHitArea:hover ~ .albumTitle {
        opacity: 1;
    }

`

const AlbumHitArea = styled.div`

    position: absolute;
    height: 200px;
    width: 200px;
    z-index: 100;

` 

const AlbumCover = styled.div`

    position: absolute;
    height: 200px;
    width: 200px;
    background: ${props => (props.cover ? "url(" + props.cover
        + ")" : "lightgray")};
    background-size: cover;

`

const Tab = styled.div`

    background: ${props => (props.currentGenre == props.tabID ? "yellow" : "white")};
    background: ${props => (props.currentGenre == "all" ? "yellow !important" : "white")};

`

const AlbumTile = styled.div`

    height: 200px;
    width: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    opacity: 0;

    h2 {
        font-weight: normal;
        opacity: 0;
        z-index: 20;
        position: absolute;
        margin: 0;
    }

    .albumCover{
        z-index: 1;
    }


`

const ReviewBox = styled.div`

    height: 20px;
    width: 20px;
    background-color: pink;
    margin: 2px;

`

class Charts extends Component {

    state = {
        articles: [],
        genre: "all",
        indieArticles: [],
    }

    createGenres = () => {
        let indieArticles =[]
        let electronicArticles =[]
        let hipHopArticles =[]

        for (var i = 0; i < this.state.articles.length; i++) {

            if (this.state.articles[i]== "indie"){

                indieArticles.push(this.state.articles[i])
                this.setState({ indieArticles: indieArticles})
            }

        }
     }

    loadArticles = (callback) => {
        const apiEndpoint = "https://sutromusic.cdn.prismic.io/api/v2";

        Prismic.api(apiEndpoint).then(api => {
            api.query(
                Prismic.Predicates.at("document.type", "article")
            ).then(response => {
                if (response) {
                    this.setState({ articles: response.results });
                }
            });
        });

        callback()

    };

    componentDidUpdate() {
        console.log(this.state.articles)
        console.log(this.state.indieArticles)

        var tl = anime.timeline({

        })

        tl.add({
            targets: '.albumContainer .albumTile',
            opacity: 1,
            margin: 0,
            delay: anime.stagger(100, {from: 'center'}),
        })
    }

    componentDidMount() {
        this.loadArticles(this.createGenres)
    }

    openSpotify = (url) => {
        window.open(url);
    }

    changeGenre(arg) {
        this.setState({ genre: arg})
    }

    calculateReviews(arg) {
        var temp = [];
        for (var i = 0; i < arg; i++) {
            temp.push(<ReviewBox/>)
        }
        console.log(temp)
        return temp
    }

    render() {
        return (
            <Wrapper className="wrapper">
                {/* <Hero></Hero> */}
                <Criterion genre={this.state.genre}>
                        <div>

                        </div>
                        <div className="categories">
                            <Tab className="tab" currentGenre={this.state.genre} tabID="all" onClick={() => this.changeGenre("all")}>All</Tab>
                            <Tab className="tab" currentGenre={this.state.genre} tabID="indie" onClick={() => this.changeGenre("indie")}>Indie</Tab>
                            <Tab className="tab" currentGenre={this.state.genre} tabID="electronic" onClick={() => this.changeGenre("electronic")}>Electronic</Tab>
                            <Tab className="tab" currentGenre={this.state.genre} tabID="hiphop" onClick={() => this.changeGenre("hiphop")}>Hip Hop</Tab>
                        </div>
                    </Criterion>
                <AlbumContainer className="albumContainer">
                    {this.state.articles && this.state.articles.map((albumReview, i) => (
                        <AlbumTile className="albumTile"
                            onClick={() => this.openSpotify(albumReview.data.spotify[0].text)}
                        >
                            <AlbumHitArea className="albumHitArea">
                            </AlbumHitArea>
                            <AlbumCover className='albumCover' cover={albumReview.data.albumcover[0].text} ></AlbumCover>
                            <h2 className="albumTitle">{albumReview.data.songname[0].text}</h2>

                        </AlbumTile>
                    )
                    )}
                </AlbumContainer>
                <AlbumReviewContainer>

                    {this.state.articles && this.state.articles.map((albumReview, i) => (
                        <AlbumReview cover={albumReview.data.albumcover[0].text}>
                            <div className="albumCover" >
                            </div>
                            <div className="albumInfo">
                                {console.log(albumReview.data.albumcover[0].text)}
                                <div className="line2">
                                    <div className="line1">
                                        <h3>{albumReview.data.songname[0].text}</h3>
                                        <h4>by {albumReview.data.bandname[0].text}</h4>
                                    </div>
                                    <div>
                                        <p>{albumReview.data.date[0].text}</p>
                                        <p>{albumReview.data.rating[0].text}</p>
                                        {() => {this.calculateReviews(albumReview.data.rating[0].text)}}
                                    </div>
                                </div>
                                <p>{albumReview.data.review[0].text}</p>
                            </div>

                        </AlbumReview>
                    ))}
                </AlbumReviewContainer>
            </Wrapper>
        );
    }

}

export default Charts;
